<?php require_once __DIR__.'/_bootstrap.php'; $rows=db()->query('SELECT m.*,COUNT(im.item_id) item_count FROM makers m LEFT JOIN item_makers im ON im.maker_id=m.id GROUP BY m.id ORDER BY m.name')->fetchAll(); include __DIR__.'/partials/header.php'; ?>
<div class="card"><h1>メーカー一覧</h1></div><div class="grid"><?php foreach($rows as $r): ?><div class="card"><a href="/public/maker.php?maker_id=<?= e((string)$r['maker_id']) ?>"><?= e($r['name']) ?></a> (<?= e((string)$r['item_count']) ?>)</div><?php endforeach; ?></div><?php include __DIR__.'/partials/footer.php'; ?>
