<?php require_once __DIR__.'/_bootstrap.php'; $rows=db()->query('SELECT s.*,COUNT(isr.item_id) item_count FROM series_master s LEFT JOIN item_series isr ON isr.series_id=s.id GROUP BY s.id ORDER BY s.name')->fetchAll(); include __DIR__.'/partials/header.php'; ?>
<div class="card"><h1>シリーズ一覧</h1></div><div class="grid"><?php foreach($rows as $r): ?><div class="card"><a href="/public/series_detail.php?series_id=<?= e((string)$r['series_id']) ?>"><?= e($r['name']) ?></a> (<?= e((string)$r['item_count']) ?>)</div><?php endforeach; ?></div><?php include __DIR__.'/partials/footer.php'; ?>
